mapper.select:
select count(*)
from ujcms_block_item t
where t.block_id_ = #{blockId}
  and t.article_id_ = #{articleId}

select

from ujcms_block_item t
where t.article_id_ = #{articleId,jdbcType=BIGINT}

select case
           when exists(
                   select *
                   from ujcms_block_item t
                   where t.block_id_ = #{blockId}
                     and t.site_id_ <> #{notSiteId}
               ) then 1
           else 0
           end as non_zero_exists

mapper.update:
update ujcms_block_item
set order_ = order_ + 1
where order_ < #{from} and order_ >= #{to}

update ujcms_block_item
set order_ = order_ - 1
where order_ > #{from} and order_ <= #{to}

update ujcms_block_item
set order_ = #{order}
where id_ = #{id}

mapper.select:
select

from ujcms_block_item t
where t.id_ = #{id,jdbcType=BIGINT}
