mapper.update:
update ujcms_form
set order_ = order_ + 1
where order_ < #{from}
  and order_ >= #{to}

update ujcms_form
set order_ = order_ - 1
where order_ > #{from}
  and order_ <= #{to}

update ujcms_form
set order_ = #{order}
where id_ = #{id}

update ujcms_form
set modified_user_id_ = #{toUserId}
where modified_user_id_ = ${fromUserId}

mapper.select:
select

from ujcms_form t
where t.id_ = #{id,jdbcType=BIGINT}
