mapper.update:
update ujcms_form_type
set order_ = order_ + 1
where order_ < #{from} and order_ >= #{to}

update ujcms_form_type
set order_ = order_ - 1
where order_ > #{from} and order_ <= #{to}

update ujcms_form_type
set order_ = #{order}
where id_ = #{id}

mapper.select:
select

from ujcms_form_type t
where t.id_ = #{id,jdbcType=BIGINT}
